import React, { useState, useEffect }  from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [inventory, setInventory] = useState([]);
    const [newPet, setNewPet] = useState({"name":"Doggy","id":"4711"});
    const [newPetCategory, setNewPetCategory] = useState({"name":"Dog","id":"4711"});

    function handlePetInputChange(e) {
        const target = e.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;
        setNewPet({
            [name]: value
        });
    }

    function handleCategoryInputChange(e) {
        const target = e.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;
        setNewPetCategory({
            [name]: value
        });
    }

    function submitPet(e) {
        e.preventDefault();
        console.log('You clicked submit.');
        const requestPayload = {
            "id": parseInt(newPet.id),
            "name": newPet.name,
            "category": {
                "id": parseInt(newPetCategory.id),
                "name": newPetCategory.name
            },
            "photoUrls": []
        };
        fetch("http://insrsaeu.apigw-aw-eu.webmethods.io/gateway/team1_petstore/1.0.5/pet", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(requestPayload) // body data type must match "Content-Type" header
        })
        .then(res => res.json())
        .then(
            (data) => {
                alert("Pet submitted: " + JSON.stringify(data))
                console.log(data);
            },
            (error) => {
                alert("Error: " + JSON.stringify(error))
                console.log(error);
            }
        )
    }

    useEffect(() => {
        fetch("http://insrsaeu.apigw-aw-eu.webmethods.io/gateway/team1_petstore/1.0.5/store/inventory")
            .then(res => res.json())
            .then(
                (data) => {
                    setIsLoaded(true);
                    setInventory(data);
                },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            )
      }, [])

    if (error) {
        return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
        return <div>Loading...</div>;
    } else {
        return (
          <div>
            <h1>Inventory</h1>
            <ul>
                <li key="available">
                        Available: <Link to={`petsbystatus/available`}>{inventory.available}</Link>
                </li>
        		<li key="pending">
                        Pending: <Link to={`petsbystatus/pending`}>{inventory.pending}</Link>
                </li>
                <li key="sold">
                        Sold: <Link to={`petsbystatus/sold`}>{inventory.sold}</Link>
                </li>
            </ul>
            <h1>Create / update pet</h1>
            <form>
                <label>
                    Name:
                    <input type="text" value={newPet.name} name="name" onChange={handlePetInputChange} />
                </label>
                <br/>
                <label>
                    ID:
                    <input type="text" value={newPet.id} name="id" onChange={handlePetInputChange} />
                </label>
                <br/>
                <label>
                    Category name:
                    <input type="text" value={newPetCategory.name} name="name" onChange={handleCategoryInputChange} />
                </label>
                <br/>
                <label>
                    Category ID:
                    <input type="text" value={newPetCategory.id} name="id" onChange={handleCategoryInputChange} />
                </label>
                <br/>
                <input type="submit" value="Submit" onClick={submitPet} />
            </form>
          </div>
        );
    }
};

export default Home;
