import React, { useState, useEffect }  from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [inventory, setInventory] = useState([]);

    useEffect(() => {
        fetch("http://insrsaeu.apigw-aw-eu.webmethods.io/gateway/team1_petstore/1.0.5/store/inventory")
            .then(res => res.json())
            .then(
                (data) => {
                    setIsLoaded(true);
                    setInventory(data);
                },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            )
      }, [])

    if (error) {
        return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
        return <div>Loading...</div>;
    } else {
        return (
          <div>
            <h1>Inventory</h1>
            <ul>
                <li key="available">
                        Available: <Link to={`petsbystatus/available`}>{inventory.available}</Link>
                </li>
        		<li key="pending">
                        Pending: <Link to={`petsbystatus/pending`}>{inventory.pending}</Link>
                </li>
                <li key="sold">
                        Sold: <Link to={`petsbystatus/sold`}>{inventory.sold}</Link>
                </li>
            </ul>
            <h1>Create / update pet</h1>
            <form>
                <label>
                    Name:
                    <input type="text" name="name" />
                </label>
                <label>
                    ID:
                    <input type="text" name="id" />
                </label>
                <label>
                    Category name:
                    <input type="text" name="name" />
                </label>
                <label>
                    Category ID:
                    <input type="text" name="id" />
                </label>
                <input type="submit" value="Submit" />
            </form>
          </div>
        );
    }
};

export default Home;
